package tco_test

import (
	"testing"

	"github.com/hashicorp/terraform-plugin-sdk/v2/helper/resource"

	tcacctest "github.com/tencentcloudstack/terraform-provider-tencentcloud/tencentcloud/acctest"
)

func TestAccTencentCloudOrganizationExternalSamlIdentityProviderResource_basic(t *testing.T) {
	t.Parallel()
	resource.Test(t, resource.TestCase{
		PreCheck: func() {
			tcacctest.AccPreCheck(t)
		},
		Providers: tcacctest.AccProviders,
		Steps: []resource.TestStep{
			{
				Config: testAccOrganizationExternalSamlIdentityProvider,
				Check: resource.ComposeTestCheckFunc(
					resource.TestCheckResourceAttrSet("tencentcloud_organization_external_saml_identity_provider.example", "id"),
					resource.TestCheckResourceAttrSet("tencentcloud_organization_external_saml_identity_provider.example", "zone_id"),
				),
			},
			{
				Config: testAccOrganizationExternalSamlIdentityProviderUpdate,
				Check: resource.ComposeTestCheckFunc(
					resource.TestCheckResourceAttrSet("tencentcloud_organization_external_saml_identity_provider.example", "id"),
					resource.TestCheckResourceAttrSet("tencentcloud_organization_external_saml_identity_provider.example", "zone_id"),
				),
			},
		},
	})
}

